package com.example.course_work_cinema.dao;import com.example.course_work_cinema.entity.Movie;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;public interface MovieRepository extends JpaRepository<Movie, Integer> {    @Query(value = "select movie_id from movie where movie.title=?1", nativeQuery = true)    Integer getMovieIdByTitle(String movieTitle);}