package com.example.course_work_cinema.dao;import com.example.course_work_cinema.entity.Seance;import com.example.course_work_cinema.entity.Ticket;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import java.util.List;public interface TicketRepository extends JpaRepository<Ticket, Integer> {    @Query(value = "select * from ticket where ticket.seance_id = ?1", nativeQuery = true)    List<Ticket> findTicketsBySeance(Integer seance_id);}