package com.example.course_work_cinema.entity;import javax.persistence.*;@Entity@Table(name = "hall")public class Hall {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "hall_number")    private int hallNumber;    @Column(name = "row_count")    private int rowCount;    @Column(name = "seat_count_in_row")    private int seatCountInRow;    public Hall() {    }    public Hall(int rowCount, int seatCountInRow) {        this.rowCount = rowCount;        this.seatCountInRow = seatCountInRow;    }    public int getHallNumber() {        return hallNumber;    }    public void setHallNumber(int hallNumber) {        this.hallNumber = hallNumber;    }    public int getRowCount() {        return rowCount;    }    public void setRowCount(int rowCount) {        this.rowCount = rowCount;    }    public int getSeatCountInRow() {        return seatCountInRow;    }    public void setSeatCountInRow(int seatCountInRow) {        this.seatCountInRow = seatCountInRow;    }    @Override    public String toString() {        return "Hall{" +                "hallNumber=" + hallNumber +                ", rowCount=" + rowCount +                ", seatCountInRow=" + seatCountInRow +                '}';    }}