package com.example.course_work_cinema.entity;import org.hibernate.annotations.CreationTimestamp;import org.springframework.format.annotation.DateTimeFormat;import javax.persistence.*;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotNull;import javax.validation.constraints.Pattern;import javax.validation.constraints.Size;import java.text.SimpleDateFormat;import java.time.LocalDate;import java.util.Date;@Entity@Table(name = "movie")public class Movie {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "movie_id")    private int movieId;    @NotBlank(message = "You need to enter movie title")    @Column(name = "title")    private String title;    @Pattern(regexp = "[0-9]+", message = "You should enter only digits numbers")    @Column(name = "length")    private String length;    @NotNull(message = "You should enter date with pattern dd-mm-yyyy")    @DateTimeFormat(pattern = "dd-MM-yyyy")    @Column(name = "show_start_date")    private Date showStartDate;    @NotNull(message = "You should enter date with pattern dd-mm-yyyy")    @DateTimeFormat(pattern = "dd-MM-yyyy")    @Column(name = "show_end_date")    private Date showEndDate;    public Movie() {    }    public Movie(String title, String length, Date showStartDate, Date showEndDate) {        this.title = title;        this.length = length;        this.showStartDate = showStartDate;        this.showEndDate = showEndDate;    }    public int getMovieId() {        return movieId;    }    public void setMovieId(int movieId) {        this.movieId = movieId;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getLength() {        return length;    }    public void setLength(String length) {        this.length = length;    }    public Date getShowStartDate() {        return showStartDate;    }    public void setShowStartDate(Date showStartDate) {        this.showStartDate = showStartDate;    }    public Date getShowEndDate() {        return showEndDate;    }    public void setShowEndDate(Date showEndDate) {        this.showEndDate = showEndDate;    }    @Override    public String toString() {        return "Movie{" +                "movieId=" + movieId +                ", title='" + title + '\'' +                ", length=" + length +                ", showStartDate=" + showStartDate +                ", showEndDate=" + showEndDate +                '}';    }}