package com.example.course_work_cinema.entity;import org.springframework.format.annotation.DateTimeFormat;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Pattern;import java.time.LocalDateTime;import java.util.Date;import java.util.List;@Entity@Table(name = "seance")public class Seance {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "seance_id")    private int seanceId;    @ManyToOne(cascade = {            CascadeType.DETACH,            CascadeType.MERGE,            CascadeType.PERSIST,            CascadeType.REFRESH})    @JoinColumn(name = "hall_number")    private Hall hallNumber;    @NotNull(message = "You should enter date with pattern dd-mm-yyyy")    @DateTimeFormat(pattern = "dd-MM-yyyy")    @Column(name = "seance_date")    private Date seanceDate;    @NotNull(message = "You should enter date with pattern hh:mm")    @Column(name = "seance_time")    private Date seanceTime;    @ManyToOne    @JoinColumn(name = "movie_id")    private Movie movieId;    public Seance() {    }    public Seance(Hall hallNumber, Date seanceDate, Date seanceTime, Movie movieId) {        this.hallNumber = hallNumber;        this.seanceDate = seanceDate;        this.seanceTime = seanceTime;        this.movieId = movieId;    }    public int getSeanceId() {        return seanceId;    }    public void setSeanceId(int seanceId) {        this.seanceId = seanceId;    }    public Hall getHallNumber() {        return hallNumber;    }    public void setHallNumber(Hall hallNumber) {        this.hallNumber = hallNumber;    }    public Date getSeanceDate() {        return seanceDate;    }    public void setSeanceDate(Date seanceDate) {        this.seanceDate = seanceDate;    }    public Date getSeanceTime() {        return seanceTime;    }    public void setSeanceTime(Date seanceTime) {        this.seanceTime = seanceTime;    }    public Movie getMovieId() {        return movieId;    }    public void setMovieId(Movie movieId) {        this.movieId = movieId;    }    @Override    public String toString() {        return "Seance{" +                "seanceId=" + seanceId +                ", hallNumber=" + hallNumber +                ", seanceDate=" + seanceDate +                ", seanceTime='" + seanceTime + '\'' +                ", movieId=" + movieId +                '}';    }}