package com.example.course_work_cinema.entity;import javax.persistence.*;import java.util.Date;@Entity@Table(name = "ticket")public class Ticket {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "ticket_id")    private int ticketId;    @ManyToOne(/*cascade = CascadeType.ALL*/)    @JoinColumn(name = "seance_id")    private Seance seanceId;    @Column(name = "row_number")    private int rowNumber;    @Column(name = "seat_number")    private int seatNumber;    @Column(name = "purchase_date")    private Date purchaseDate;    public Ticket() {    }    public Ticket(Seance seanceId, int rowNumber, int seatNumber, Date purchaseDate) {        this.seanceId = seanceId;        this.rowNumber = rowNumber;        this.seatNumber = seatNumber;        this.purchaseDate = purchaseDate;    }    public int getTicketId() {        return ticketId;    }    public void setTicketId(int ticketId) {        this.ticketId = ticketId;    }    public Seance getSeanceId() {        return seanceId;    }    public void setSeanceId(Seance seanceId) {        this.seanceId = seanceId;    }    public int getRowNumber() {        return rowNumber;    }    public void setRowNumber(int rowNumber) {        this.rowNumber = rowNumber;    }    public int getSeatNumber() {        return seatNumber;    }    public void setSeatNumber(int seatNumber) {        this.seatNumber = seatNumber;    }    public Date getPurchaseDate() {        return purchaseDate;    }    public void setPurchaseDate(Date purchaseDate) {        this.purchaseDate = purchaseDate;    }    @Override    public String toString() {        return "Ticket{" +                "ticketId=" + ticketId +                ", seanceId=" + seanceId +                ", rowNumber=" + rowNumber +                ", seatNumber=" + seatNumber +                ", purchaseDate=" + purchaseDate +                '}';    }}