package com.example.course_work_cinema.model;import org.springframework.format.annotation.DateTimeFormat;import org.springframework.stereotype.Component;import java.util.Date;public class SortModel {    private String title;    @DateTimeFormat(pattern = "dd-MM-yyyy")    private Date dateFrom;    @DateTimeFormat(pattern = "dd-MM-yyyy")    private Date dateTo;    public SortModel() {    }    public SortModel(String title, Date dateFrom, Date dateTo) {        this.title = title;        this.dateFrom = dateFrom;        this.dateTo = dateTo;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public Date getDateFrom() {        return dateFrom;    }    public void setDateFrom(Date dateFrom) {        this.dateFrom = dateFrom;    }    public Date getDateTo() {        return dateTo;    }    public void setDateTo(Date dateTo) {        this.dateTo = dateTo;    }    @Override    public String toString() {        return "SortModel{" +                "title='" + title + '\'' +                ", dateFrom='" + dateFrom + '\'' +                ", dateTo='" + dateTo + '\'' +                '}';    }}