package com.example.course_work_cinema.service;import com.example.course_work_cinema.dao.MovieRepository;import com.example.course_work_cinema.entity.Movie;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class MovieServiceImpl implements MovieService{    @Autowired    private MovieRepository movieRepository;    @Override    public List<Movie> findAll() {        return movieRepository.findAll();    }    @Override    public void save(Movie movie) {        movieRepository.save(movie);    }    @Override    public Optional<Movie> findById(Integer integer) {        return movieRepository.findById(integer);    }    @Override    public void deleteById(Integer integer) {        movieRepository.deleteById(integer);    }    @Override    public Integer getMovieIdByTitle(String movieTitle) {        return movieRepository.getMovieIdByTitle(movieTitle);    }}