package com.example.course_work_cinema.service;import com.example.course_work_cinema.dao.SeanceRepository;import com.example.course_work_cinema.entity.Seance;import com.example.course_work_cinema.model.SortModel;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Date;import java.util.List;import java.util.Optional;@Servicepublic class SeanceServiceImpl implements SeanceService{    @Autowired    private SeanceRepository seanceRepository;    @Override    public List<Seance> findAll() {        return seanceRepository.findAll();    }    @Override    public void save(Seance seance) {        seanceRepository.save(seance);    }    @Override    public Optional<Seance> findById(Integer integer) {        return seanceRepository.findById(integer);    }    @Override    public void deleteById(Integer integer) {        seanceRepository.deleteById(integer);    }    @Override    public List<Seance> findAllSeanceByDate(Date date, Integer hallNumber) {        return seanceRepository.findAllSeanceByDate(date, hallNumber);    }    @Override    public List<Seance> sortSeances(Integer movieId) {        return seanceRepository.sortSeances(movieId);    }    @Override    public List<Seance> sortSeances() {        return seanceRepository.sortSeances();    }}