package com.example.course_work_cinema.service;import com.example.course_work_cinema.dao.TicketRepository;import com.example.course_work_cinema.entity.Ticket;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class TicketServiceImpl implements TicketService{    @Autowired    private TicketRepository ticketRepository;    @Override    public List<Ticket> findAll() {        return ticketRepository.findAll();    }    @Override    public void save(Ticket ticket) {        ticketRepository.save(ticket);    }    @Override    public Optional<Ticket> findById(Integer integer) {        return ticketRepository.findById(integer);    }    @Override    public void deleteById(Integer integer) {        ticketRepository.deleteById(integer);    }    @Override    public List<Ticket> findTicketsBySeance(Integer seance_id) {        return ticketRepository.findTicketsBySeance(seance_id);    }}