package com.example.course_work_cinema.utils;import com.example.course_work_cinema.entity.Seance;import com.example.course_work_cinema.entity.Ticket;import com.example.course_work_cinema.service.TicketService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import java.util.List;@Componentpublic class TicketUtils {    @Autowired    private TicketService ticketService;    public boolean isSeatTaken(Ticket ticket) {        List<Ticket> ticketList = ticketService.findTicketsBySeance(ticket.getSeanceId().getSeanceId());        for (Ticket item : ticketList) {            if (ticket.getRowNumber() == item.getRowNumber()                    && ticket.getSeatNumber() == item.getSeatNumber()) {                return false;            }        }        return true;    }}